import { StyleSheet, Text, View, Image } from 'react-native'
import React from 'react'
import ImageAvatar from '../components/ImageAvatar'
import SvgDots from './../components/Svgs/Dots';

const UserCard = () => {
  return (
    <View style={styles.container}>
        <View style={styles.wrapper}>
            <View style={styles.left}>
                <ImageAvatar size={36} borderRadius={21} src={require('../assets/avater.png')}/>
                <Text style={styles.name}>Shapon Pal Shapon Pal</Text>
            </View>
            <View style={styles.right}>
                <SvgDots/>
            </View>
        </View>
        
        <View style={styles.actions}>
            {/* <Image size={24} source={require('../assets/icons/dots.svg')} /> */}
        </View>
      </View>
  )
}

export default UserCard

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#F7F7F7',
        borderRadius: 8,
        margin: 5,
        padding: 10
    },
    wrapper: {
        display: 'flex',
        flexDirection: 'row',
        justifyContent: "space-between",
        alignItems: "top",
    },
    left:{
        display: 'flex',
        flexDirection: 'row',
        width: '90%',

    },
    right: {
        display: 'flex',
        flexDirection: 'row',
    },
    name:{
        fontSize: 14,
        lineHeight: 17,
        fontWeight: '500',
        color: '#1B1A57',
        paddingLeft: 8,
        flexWrap: 'wrap'
    },
    actions:{
        display: 'flex',
        flexDirection: 'row',
        justifyContent: "space-between",
        alignItems: "center",
    }
})
